import React, { useEffect, useState } from 'react'
import styled from "styled-components"
import { useParams } from "react-router-dom"
import db from "../firebase"

function Detail  () {
    const { id } = useParams();
    const [movie, setMovie] = useState({}); //atento a las llaves dentro de los parentesis del useState sin eso tira error!!!

    useEffect(() => {
      db.collection("movies")
        .doc(id)
        .get()
        .then((doc) => {
          if (doc.exists) {
            setMovie(doc.data());
          } else {
            console.log("no such document in firebase ðŸ”¥");
          }
        })
        .catch((error) => {
          console.log("Error getting document:", error);
        });
    }, []);

    return (
        <Container>
            <Background>
                <img src={movie.backgroundImg} alt="img"/>
            </Background>
            <ImageTitle>
                <img src={movie.titleImg} alt="img"/>
            </ImageTitle>
            <Controls>
                <PlayButton>
                        <img src="/images/play-icon-black.png" alt="img"/>
                        <span>PLAY</span>
                </PlayButton>
                <TrailerButton>
                <img src="/images/play-icon-white.png" alt="img"/>
                    <span>TRAILER</span>
                </TrailerButton>
                <AddButton>
                    <span>+</span>
                </AddButton>
                <GroupWatchButton>
                    <img src="/images/group-icon.png" alt="img"/>
                </GroupWatchButton>
            </Controls>
            <SubTitle>
               {movie.subTitle}  
            </SubTitle>
            <Description>
                {movie.description}
            </Description>
        </Container>
    )
}

export default Detail

const Container = styled.div`
    position: relative;
    min-height: calc(100vh - 250px);
    overflow-x: hidden;
    display: block;
    padding: 0 calc(3.5vw + 5px);
`

const Background = styled.div`
    position: fixed;
    top: 0;
    left: 0;

    right: 0;
    z-index: -1;
    opacity: 0.8;


    img{
    width: 100vw;
    height: 100vh;
    object-fit: cover;
    }

`
const ImageTitle = styled.div`
  align-items: flex-end;
  display: flex;

  justify-content: flex-start;
  margin: 0px auto;
  height: 23vw;
  min-height: 170px;
  padding-bottom: 24px;
  width: 100%;
  img {
    max-width: 600px;
    min-width: 200px;
    width: 35vw;
  }
`;

const Controls = styled.div`
    display: flex;
    align-items: center;
    flex-flow: row nowrap;
    margin: 24px 0px;
    min-height: 56px;
`
const PlayButton = styled.button`
    border-radius: 4px;
    font-size: 15px;
    padding: 0px 24px;
    margin-right: 22px;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 56px;
    background-color: rgb(249,249,249);
    border: none; 
    letter-spacing: 1.8px;
    cursor: pointer;

    img {
    width: 32px;
    }

    &:hover {
        background-color: rgb(198,198,198);
    }

`
const TrailerButton = styled(PlayButton)`
    background:rgba(0,0,0,0.3);
    border: 1px solid white;
    color: white;


`
const AddButton = styled.button`
    margin-right: 16px;
    width: 44px;
    height: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    border: 1.5px solid white;
    background-color: rgba(0,0,0,0.6);
    cursor: pointer;
    span {
        font-size: 30px;
        color: white;
    }
`
const GroupWatchButton = styled(AddButton)`
    background-color: black;

`
const SubTitle = styled.div`
    color: rgb(249, 249,249);
    font-size: 15px;
    min-height: 20px;
    margin-top: 26px;


`

const Description = styled.div`
    line-height: 1.4;
    font-size: 20px;
    margin-top: 16px;
    color: rgb(249, 249,249);
    max-width: 760px;

`
