import React from 'react'
import styled from "styled-components"
import { selectUserName, selectUserPhoto, } from "../features/user/userSlice"
import { useSelector } from "react-redux"


function Header() {
    const userName = useSelector(selectUserName);
    const userPhoto = useSelector(selectUserPhoto);

    return (
        <Nav>
            <Logo src="/images/logo.svg"/>
            <NavMenu>
            <a>
              <img src="/images/home-icon.svg" alt="HOME" alt="img"/>
              <span>HOME</span>
            </a>
            <a>
              <img src="/images/search-icon.svg" alt="SEARCH" alt="img"/>
              <span>SEARCH</span>
            </a>
            <a>
              <img src="/images/watchlist-icon.svg" alt="WATCHLIST" alt="img"/>
              <span>WATCHLIST</span>
            </a>
            <a>
              <img src="/images/original-icon.svg" alt="ORIGINALS" alt="img"/>
              <span>ORIGINALS</span>
            </a>
            <a>
              <img src="/images/movie-icon.svg" alt="MOVIES" alt="img"/>
              <span>MOVIES</span>
            </a>
            <a>
              <img src="/images/series-icon.svg" alt="SERIES" alt="img"/>
              <span>SERIES</span>
            </a>
            </NavMenu>
            <UserImg src="https://i.guim.co.uk/img/static/sys-images/Guardian/Pix/pictures/2004/09/16/2ramones1.jpg?width=300&quality=45&auto=format&fit=max&dpr=2&s=77791434228523055167af4c48e24ad1" alt="img"/>
        </Nav>
    )
}

export default Header

const Nav = styled.nav`
    height: 70px;
    background-color: #090b13;
    display: flex;
    align-items: center;
    padding: 0 36px;
    overflow-x: hidden;
`
const Logo = styled.img`
    width: 80px;
`
const NavMenu = styled.div`
    display: flex;
    flex: 1;
    margin-left: 25px;
    align-items: center;


    a {
        display: flex;
        align-items: center;
        padding: 0 12px;
        cursor: pointer;

        img {
            height: 20px;
        }
        span {
            font-size: 13px;
            letter-spacing: 1.42px;
            position: relative;

            &:after {
                content:"";
                height: 2px;
                background-color: white;
                border-radius: 0px 0px 4px 4px;
                position: absolute;
                left: 0;
                right: 0;
                bottom: -6px;
                opacity: 0;
                transform-origin: left center;
                transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
                transform: scaleX(0);
            }
        }
        &:hover {
            span:after {
            transform: scaleX(1);
            opacity: 1;
            }
        }
    }

`
const UserImg = styled.img`
    width: 48px;
    height: 48px;
    border-radius: 50%;
    cursor: pointer;

`
